+fifthy*2<br>&emsp;Ex = endx-fifthx*2<br>&emsp;Ey = endy-fifthy*2<br>&emsp;Hx = endx-fifthx<br>&emsp;Hy = endy-fifthy<br>&emsp;Bx = Ax-fifthy<br>&emsp;By = Ay+fifthx<br>&emsp;Cx = Dx-fifthy<br>&emsp;Cy = Dy+fifthx<br>&emsp;Fx = Ex+fifthy<br>&emsp;Fy = Ey-fifthx<br>&emsp;Gx = Hx+fifthy<br>&emsp;Gy = Hy-fifthx<br>&emsp;line(startx,starty, Ax,Ay)<br>&emsp;line(Ax,Ay, Bx,By)<br>&emsp;line(Bx,By, Cx,Cy)<br>&emsp;line(Cx,Cy, Dx,Dy)<br>&emsp;line(Dx,Dy, Ex,Ey)<br>&emsp;line(Ex,Ey, Fx,Fy)<br>&emsp;line(Fx,Fy, Gx,Gy)<br>&emsp;line(Gx,Gy, Hx,Hy)<br>&emsp;line(Hx,Hy, endx,endy)<br><br>tur.speed(0)<br>tur.ht()<br>tur.color(<span class=green>"cyan"</span>)<br>tur.bgcolor(<span class=green>"black"</span>)<br><span class=orange>while True</span>:<br>&emsp;line(-600,0,600,0)<br>&emsp;time.sleep(10)<br>&emsp;tur.clear()<br></code></a><div class=image><a target=blank href=/turtle/fractals/images/fractal.jpg><span class="tooltip_ mouse">Fractal</span><img src="images/fractal.jpg alt="fractal along a line"></a></div><div class=vid><iframe src="https://youtube.com/embed/R--ngYcAWmI?mute=1" allow=autoplay ; encrypted-media allowfullscreen>Video Unavailable</iframe></div></div><div id=quadratic class="box colour-3"><div class=h2><h2>Quadratic Koch Curve</h2></div> <a href="/turtle/pyscripts/Quadratic Koch Curve.py" target=blank class=code download><code><span class="tooltip_ mouse">Click to download</span><h3>Code</h3> <span class=orange>import</span> turtle <span class=orange>as</span> tur<br><span class=orange>import</span> math, time<br><span class=orange>def</span> <span class=blue>line</span>(startx,starty, endx,endy):<br>&emsp;fullx,fully = endx-startx,endy-starty<br>&emsp;distance = math.sqrt(fully**2+fullx**2)<br>&emsp;<span class=orange>if</span> distance < 5:<br>&emsp;&emsp;tur.pu()<br>&emsp;&emsp;tur.goto(startx,starty)<br>&emsp;&emsp;tur.pd()<br>&emsp;&emsp;tur.goto(endx,endy)<br>&emsp;&emsp;<span class=orange>return</span><br>&emsp;thirdx = fullx/3<br>&emsp;thirdy = fully/3<br>&emsp;Ax = startx+thirdx<br>&emsp;Ay = starty+thirdy<br>&emsp;Dx = endx-thirdx<br>&emsp;Dy = endy-thirdy<br>&emsp;Bx = Ax-thirdy<br>&emsp;By = Ay+thirdx<br>&emsp;Cx = Dx-thirdy<br>&emsp;Cy = Dy+thirdx<br>&emsp;line(startx,starty, Ax,Ay)<br>&emsp;line(Ax,Ay, Bx,By)<br>&emsp;line(Bx,By, Cx,Cy)<br>&emsp;line(Cx,Cy, Dx,Dy)<br>&emsp;line(Dx,Dy, endx,endy)<br><br>tur.speed(0)<br>tur.ht()<br>tur.color(<span class=green>"cyan"</span>)<br>tur.bgcolor(<span class=green>"black"</span>)<br><span class=orange>while True</span>:<br>&emsp;line(-600,0,600,0)<br>&emsp;time.sleep(10)<br>&emsp;tur.clear()<br></code></a><div class=image><a target=blank href="/turtle/fractals/images/quadratic koch curve.jpg"><span class="tooltip_ mouse">Quadratic Koch Curve</span><img src="images/quadratic koch curve.jpg" alt="fractals along a line"></a></div><div class=vid><iframe src="https://youtube.com/embed/47VIU0mfGLI?mute=1" allow=autoplay ; encrypted-media allowfullscreen>Video Unavailable</iframe></div></div><div id=sausage class="box colour-4"><div class=h2><h2>Minkowski Sausage</h2></div> <a href="/turtle/pyscripts/Minkowski Sausage.py" target=blank class=code download><code><span class="tooltip_ mouse">Click to download</span><h3>Code</h3> <span class=orange>import</span> turtle <span class=orange>as</span> tur<br><span class=orange>import</span> math, time<br><span class=orange>def</span> <span class=blue>line</span>(startx,starty, endx,endy):<br>&emsp;fullx,fully = endx-startx,endy-starty<br>&emsp;distance = math.sqrt(fully**2+fullx**2)<br>&emsp;<span class=orange>if</span> distance < 5:<br>&emsp;&emsp;tur.pu()<br>&emsp;&emsp;tur.goto(startx,starty)<br>&emsp;&emsp;tur.pd()<br>&emsp;&emsp;tur.goto(endx,endy)<br>&emsp;&emsp;<span class=orange>return</span><br>&emsp;quarterx = fullx/4<br>&emsp;quartery = fully/4<br>&emsp;Ax = startx+quarterx<br>&emsp;Ay = starty+quartery<br>&emsp;Mx = startx+quarterx*2<br>&emsp;My = starty+quartery*2<br>&emsp;Fx = endx-quarterx<br>&emsp;Fy = endy-quartery<b